import React,{useState,useEffect} from 'react'
 import "./HomePage.css"

import axios from 'axios';
import { Link } from 'react-router-dom';

function HomePage() {
    let temp=[];

    const[data,setdata]=useState([])
    const[count,setcount]=useState(0)
    const[cart,setCart]=useState([])


    useEffect(()=>{
        axios.get("https://fakestoreapi.com/products")
        .then(res=>{
            // console.log(res.data);
            setdata(res.data);
        })
        .catch((err)=>{
            console.log(err);
        })
    },[])


    const increment=(index)=>{

    //         console.log(index);
    //     const result=data.filter((data)=> data.id==index+1)
    //         console.log(result);

    }

    const decrement=()=>{
        setcount((prev)=>prev-1)
    }

    const addToCart=(index)=>{
        const result=data.filter((data)=> data.id==index+1)
        console.log(result[0]);

        temp.push(result[0])

        // console.log("temp",temp);

        setCart(...cart,temp)
        console.log("array",cart);

    }


  return (
    <div>
        <div className='cartbtn'>
         <Link to='/homepage/cart'>   
         <button > Cart</button>   
         </Link>
        </div>


        <div className="cls1">
        {        
        data!==null && data.map((data1,index)=>
                <div className="card" key={index} style={{width:"18rem", height:"15rem",margin:"10px"}}>
                    {/* <h5 className="card-id">{data!=null && data[index].id}</h5> */}
                 <div> 
                <img src={data!=null && data[index].image} className="card-img-top" alt="..."/>
                </div>
                    <div className="card-body">

                      {/* {console.log(index)}   */}
                    <h5 className="card-title">{data!=null && data[index].title}</h5>
                    <p className="card-text">₹{data!=null && data[index].price}</p>
                </div>
                   <button className='buybtn' onClick={()=>addToCart(index)} > Add to Cart</button>    
                   <button className='plusbtn'onClick={()=>increment(index)} >+</button>    
                <label className="qtylbl">{count}</label>
                <button className='minusbtn' onClick={()=>decrement(index)}>-</button>    
           </div>
        )    
    }
        </div>
    </div>

  )
}

export default HomePage
