import React, { useState, useEffect } from 'react'
import "./HomePage.css"

import axios from 'axios';
import { Link } from 'react-router-dom';

function HomePage() {
    let temp1 = [];

    const [data, setdata] = useState([])
    const [count, setcount] = useState(0)
    const [cart, setCart] = useState([])
    const [count2, setcount2] = useState(0)
    const [quantity, setquantity] = useState([])


    useEffect(() => {
        axios.get("https://fakestoreapi.com/products")
            .then(res => {
                // console.log(res.data);
                setdata(res.data);
            })
            .catch((err) => {
                console.log(err);
            })
    }, [])


    useEffect(() => {

    }, [count2])

    const increment = (id, index) => {

        // data.map((dat,index)=>{
        // console.log(data[index].id);
        // })

        const result = data.filter((dat, index) => data[index].id == id)
        console.log(result);
        // })

        setcount((prev) => prev + 1)

        //     const result=data.filter((data)=> data.id==index+1)
        //         console.log(result);

    }

    const decrement = () => {
        setcount((prev) => prev - 1)
    }

    const addToCart = (id) => {
        // setcount2((prev) => prev + 1)
        const result = data.filter((data) => data.id == id)
        // console.log(result[0]);


        // const productexists=cart.find((item)=> cart.id===id)

        // console.log(productexists);


        let temp;
        temp = result[0]
        setCart([...cart, temp])
        alert("item added to cart")
    }


    // const addToCart=(index)=>{
    //     setcount2((prev)=>prev+1)        
    //     const result=data.filter((data)=> data.id==index+1)
    //     // console.log(result[0]);

    //     let temp ;
    //     temp=result[0]
    //      setCart([...cart,temp])
    //     alert("item added to cart")
    //     }
    // console.log("array",cart);
    localStorage.setItem("product", JSON.stringify(cart))

    return (
        <div>
            {/* <div className='cartbtn'>
                <Link to='/homepage/cart'>
                    <button > Cart</button>
                </Link>
            </div> */}


            <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <a className="navbar-brand" href="#">Home</a>
  <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
    <span className="navbar-toggler-icon"></span>
  </button>

  <div className="collapse navbar-collapse" id="navbarSupportedContent">
    <ul className="navbar-nav mr-auto">
    </ul>
    <form className="form-inline my-2 my-lg-0">
      {/* <input className="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search"/> */}

      <button className="searchbtn" type="submit">Search</button>
    </form>
  </div>
</nav>

            <div className="cls1">
                {
                    data !== null && data.map((data1, index) =>
                        <div className="card" key={index} style={{ width: "18rem", height: "15rem", margin: "10px" }}>
                            {/* <h5 className="card-id">{data!=null && data[index].id}</h5> */}
                            <div>
                                <img src={data[index].image} className="card-img-top" alt="..." />
                            </div>
                            <div className="card-body">

                                {/* {console.log(index)}   */}
                                <h5 className="card-title">{data[index].title}</h5>
                                <p className="card-text">₹{data[index].price}</p>
                            </div>
                            <button className='buybtn' onClick={() => addToCart(data[index].id)} > Add to Cart</button>
                            <button className='plusbtn' onClick={() => increment(data[index].id)} >+</button>
                            <label className="qtylbl">{count}</label>
                            <button className='minusbtn' onClick={() => decrement(index)}>-</button>
                        </div>
                    )
                }
            </div>
        </div>

    )
}

export default HomePage
